function MBanner(b,a,c){this.b1=b;this.bannerBox=a;this.oLi=c;this.num=0;this.touchsLeft=0;this.boxLeft=0;this.clickTime=0;this.timer=null;this.btu=true;this.init()}MBanner.prototype.init=function(){this.bannerBox.style.width=this.oLi[0].offsetWidth*this.oLi.length+"px";var a=this;this.bannerBox.ontouchstart=function(){a.moveStart()}};MBanner.prototype.moveStart=function(){var a=event.changedTouches[0];this.touchsLeft=a.pageX;this.boxLeft=this.bannerBox.offsetLeft;this.clickTime=Date.now();clearInterval(this.timer);var b=this;this.bannerBox.ontouchmove=function(){b.moving()};this.bannerBox.ontouchend=function(){b.moveEnd()};return false};MBanner.prototype.moving=function(){event.cancelBubble=true;event.preventDefault();var a=event.changedTouches[0];if(this.bannerBox.offsetLeft>=0){if(this.btu){this.btu=false;this.touchsLeft=a.pageX}this.bannerBox.style.left=(a.pageX-this.touchsLeft)/3+this.boxLeft+"px"}else{if(this.bannerBox.offsetLeft<=this.b1.offsetWidth-this.bannerBox.offsetWidth){if(this.btu){this.btu=false;this.touchsLeft=a.pageX}this.bannerBox.style.left=(a.pageX-this.touchsLeft)/3+this.boxLeft+"px"}else{this.bannerBox.style.left=a.pageX-this.touchsLeft+this.boxLeft+"px"}}};MBanner.prototype.moveEnd=function(){event.cancelBubble=true;var a=event.changedTouches[0];if(this.touchsLeft<a.pageX){if(this.num!=0){if(a.pageX-this.touchsLeft>this.oLi[0].offsetWidth/2||Date.now()-this.clickTime<300&&a.pageX-this.touchsLeft>30){this.num--}}startMove(this.bannerBox,{"left":-this.oLi[0].offsetWidth*this.num},400,"easeOut")}else{if(this.num!=this.oLi.length-1){if(this.touchsLeft-a.pageX>this.oLi[0].offsetWidth/2||Date.now()-this.clickTime<300&&this.touchsLeft-a.pageX>30){this.num++}}startMove(this.bannerBox,{"left":-this.oLi[0].offsetWidth*this.num},400,"easeOut")}this.touchsLeft=0;this.boxLeft=0;this.bannerBox.ontouchmove=null;this.bannerBox.ontouchend=null};