function ajax(ajax){var xhr;if(window.XMLHttpRequest){xhr=new XMLHttpRequest();}else{xhr=new ActiveXObject("Microsoft.XMLHTTP");}if(!ajax.method||ajax.method=="get"){var data=ajax.data||"";xhr.open("get",ajax.url+"?"+data+"&"+new Date().getTime(),"true");xhr.send();}else if(ajax.method=="post"){var data=ajax.data||"";xhr.open("post",ajax.url,"true");xhr.setRequestHeader('content-type','application/x-www-form-urlencoded');xhr.send(data);}xhr.onreadystatechange=function(){if(xhr.readyState==4){if(xhr.status==200){ajax.callback(xhr.responseText);}else{ajax.callback("请求出错, 服务器状态码:"+xhr.status);}}}}